@import url('https://fonts.googleapis.com/css2?family=Bebas+Neue&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    font-size: 10px;
    background-color: $cream;
}

html, body {
    width: 100vw;
    height: 100vh;
    display: grid;
}
main, section {
    width: 100vw;
    height: 100vh;
}

.main-home, .section-home {
    display: grid;
    position: relative;
    z-index: 1;
    position: relative;
    justify-content: center;
    align-items: center;
    text-align: center;
}

.button {
    position: relative;
    width: 13rem;
    height: 6rem;
    margin: auto;
    font-size: 4rem;
    font-family: 'Bebas Neue', cursive;
    font-weight: 400;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    letter-spacing: 0.1rem;
    color: white;
    background: $pink;
    border: 2px solid $black;
    transition: all 800ms ease-in-out;

    a {
        color: white;
        text-decoration: none;
    }
}

