.main-player, .section-player {
    margin: auto;
    display: grid;
    position: relative;
    z-index: 1;
    justify-content: center;
    align-content: center;
}

.section-player {
    grid-gap: 5rem;

    .player-name {
        position: relative;
    
        h1 {
            font-size: 4rem;
            font-family: 'Bebas Neue', cursive;
            font-weight: 400;
            text-align: center;
            letter-spacing: 0.1rem;
            color: $black;
        }
    
        input {
            position: relative;
            display: grid;
            align-items: center;
            bottom: 0;
            width: 35rem;
            height: 4rem;
            padding: 0 0 0 .5rem;
            outline: none;
            border: 2px solid $black;
            font-size: 2rem;
            font-family: 'Bebas Neue', cursive;
            font-weight: 400;
            letter-spacing: 0.1rem;
        }
        
        .shadow-input {
            position: absolute;
            width: 35rem;
            height: 4rem;
            bottom: -.4rem;
            left: .4rem;
            background-color: $black;
            z-index: -1;
        }
    
    }
    
    .button-div {
        position: relative;
        margin: 0rem 0 0 0;
    
        .button {
            position: relative;
            width: 13rem;
            height: 6rem;
            margin: auto;
            font-size: 4rem;
            font-family: 'Bebas Neue', cursive;
            font-weight: 400;
            display: grid;
            align-items: center;
            justify-content: center;
